INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0439885', 'The Go Master', 2006, 'Zhuangzhuang Tian', '/n7qoMTJSZkIMrtHvLcmt5XB3lTl.jpg', null, 'The Go Master is a 2006 biopic by director Tian Zhuangzhuang of renowned twentieth century Go master Wu Qingyuan, better known by his adopted name of Go Seigen. The film, which premiered at the 44th New York Film Festival, focuses on the life of this extraordinary player from his meteoric rise as a child prodigy to fame and fortune as a revolutionary strategic thinker, as well as the tumultuous global conflicts between his homeland and his adopted nation. The film also features a scene involving the Atomic bomb go game.', '/A5lCbGUN3LkbqhAxBrxQw06KOxv.jpg', null, 0);
