INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0441748', 'Cyxork 7', 2006, 'John Huff', '/6uCQejoKXmlfngKpYbFb7SfqyOK.jpg', null, 'RAY WISE and SONYA SMITH star as washed-up action hero Kommander 88 and struggling filmmaker Angela LaSalle in this dark and twisted comedy. They''re filming "Cyxork 7," latest sequel in a haggard sci-fi franchise--this time shooting at the epicenter of a predicted cataclysmic earthquake. Free special effects! Risking their lives for the buzz, they''re desperate to grab Hollywood''s attention. That is: if they will survive.', '/lZDAfOc1dM3U7JkKChXT3u1mQw1.jpg', null, 0);
