INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0441781', 'Piece by Piece', 2005, 'Nic Hill', null, null, 'Piece by Piece is a groundbreaking film that documents San Francisco''s highly controversial graffiti art movement. A story told by those who live the experience, Piece by Piece offers an intimate journey into the most intriguing and misunderstood artistic movement of modern youth culture. By detailing the last 20 years of San Francisco''s graffiti this tale offers the most candid and accurate story behind the writing on the wall in Northern California. Never has such an in-dept and balanced document been created representing San Francisco graffiti. 100 hours of footage and interviews have been collected for over 4 years and have finally been edited into a cohesive documentary film.', '/bTswjsHbS0xmyzZOFcGbaoy8pcm.jpg', null, 0);
