INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0444698', 'This Divided State', 2005, 'Steven Greenstreet', '/paXbNVT5a9TBPT7fps1omNG7k4S.jpg', null, 'This Divided State follows the controversy surrounding Utah Valley State College''s invitation to liberal filmmaker Michael Moore to speak on campus. Though UVSC is located in one of the most conservative counties in the United States, vehement opposition to Moore''s visit was much greater than anticipated. Equally surprising, however, was the overwhelming support for Moore, vocalized by students and community members previously considered "apathetic." Debate between Moore supporters and Moore protestors raged openly in the media and public forums. Death threats, hate mail, bribes, and lawsuits were all candidly captured on film.', '/wgfKym0ZbecHhUTyxtvASBfcrct.jpg', null, 0);
