INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0445054', '7 Days', 2010, 'Daniel Grou', '/brHZ66LYp05LGtm8W814WNMzwZT.jpg', null, 'Bruno Hamel is a thirty eight year old surgeon. He lives in Drummondville with his wife Sylvie, and their eight year-old daughter Jasmine. Like many happy people, he is leading an uneventful life until a beautiful fall afternoon, when his daughter is raped and murdered. From then on, the world of the Hamel family collapses. When the murderer is arrested, a terrible project germinates in Bruno''s darkened mind. He plans to capture the "monster" and make him pay for his crime. The day the murderer appears in Court, Hamel, who had prepared his plan in great detail, kidnaps the monster and later sends the police a brief message stating that the rapist and murderer of his daughter was going to be tortured for 7 days and then executed. Once this task accomplished, he will then give himself up.', '/bjgrjRhT0hCcXEgbEkBWfyYmRW.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0445056', 'Sheesha', 2005, 'Ashu Trikha', '/fa7fd3ZpzcA1mGDmWQk0cXlzdAo.jpg', null, 'Businesswoman Sia Malhotra lives a wealthy lifestyle in Bangkok, along with her twin, Ria, who is deaf and dumb due to a neurological disorder. She meets with Raj Oberoi, and both fall in love. Their endeavors to cure Ria are all in vain. Sia plans to travel to the United States to improve her business, and has no one to look after Ria. So she decides to get married to Raj so that he could move in with Ria and look after her. Raj agrees, and both obtain a marriage certificate from Pattaya City Hall, and Sia departs for America. Sia does not know that after her return she will be a suspect in the brutal killing of a female Caucasian, Diana, and will not be permitted to leave Bangkok; and she will face further trauma and shock when she finds that her husband has been sexually molesting her handicapped sister.', '/lOPGacgbmAcLiHzVpNPZujj7LxP.jpg', null, 0);
