INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0468489', 'Half Nelson', 2006, 'Ryan Fleck', '/r4y4g6PjIoYPZHAwXtP9MDs4f9o.jpg', null, 'Despite his dedication to the junior-high students who fill his classroom, idealistic teacher Dan Dunne leads a secret life of addiction that the majority of his students will never know. But things change when a troubled student Drey makes a startling discovery of his secret life, causing a tenuous bond between the two that could either end disastrously or provide a catalyst of hope.', '/eWkthzAl4NSEFlKHo3FU36O6NYk.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0468491', 'Hit Me', 2005, 'Michael Kinney', null, null, 'Dan Selig is a bored thirty year-old math wiz who''s looking for a way to change his life. His ray of light comes in the form of a curvy blonde named Alicia, who recruits Dan to join her blackjack card counting team for a one night Las Vegas score. Dan accepts the offer and meets Thomas, a Texas cowboy with a violent past. Alicia trains Dan and Thomas in the art of card counting and as the team hits the felt in Sin City, we learn that the perfect crime isn''t so perfect. When things go from bad to worse, Dan will find out the hard way that with millions up for grabs, the only question is, who''s playing who?', '/dJNWENUHTsGNX5McNXEIWyyFSWx.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0468492', 'The Host', 2006, 'Joon-ho Bong', '/egxBMOjQ9JWRM1OyxEyF7IWkIkA.jpg', null, 'Gang-du is a dim-witted man working at his father''s tiny snack bar near the Han River. One day, Gang-du''s one and only daughter Hyun-seo comes back from school irritated. She is angry at her uncle, Nam-il, who visited her school as her guardian shamelessly drunk. Ignoring her father''s excuses for Nam-il, Hyun-seo is soon engrossed in her aunt Nam-joo''s archery tournament on TV. Meanwhile, outside of the snack bar, people are fascinated by an unidentified object hanging onto a bridge. In an instant, the object reveals itself as a terrifying creature turning the riverbank into a gruesome sea of bloodÂ¡Â¦ Amid the chaos, Hyun-seo is helplessly snatched up by the creature right before Gang-du''s eyes. These unforeseen circumstances render the government powerless to act. But receiving a call of help from Hyun-seo, the once-ordinary citizen Gang-du and his family are thrust into a battle with the monster to rescue their beloved Hyun-seo.', '/74OkcSKTs4f8myeLaVdJ57lj2CG.jpg', null, 0);
