INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0473043', 'Hell to Pay', 2005, 'Chris McIntyre', '/8M03o1b6wbQnXMfYQvhGQobGfUg.jpg', null, 'Ten legendary Western stars are reunited in this action-packed tale of brothers at odds-one a decorated soldier and reluctant hero, the other a gambler who keeps company with card sharks and corrupt women-in a small mining town torn apart by civil war. When they fall for the same woman, the brothers become divided; and one man is left to face a band of ruthless killers on his own. A tribute to the great American Western, Hell to Pay is a gritty and mesmerizing tale of love, honor, duty, treachery and betrayal.', '/xkLj4ut8pA8aeDcQqvP7BDLlz32.jpg', null, 0);
