INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0475590', 'Scary Hair', 2005, 'Shin-yeon Won', '/okeadfAPJVOA0IKLflCM1VkExe3.jpg', null, 'When Chi-Hyon takes home her young sister, Su Hyon, a terminal cancer patient, so that she can help her enjoy her remaining time on earth, she buys her a wig to mask the effects of chemotherapy -- not realizing that the wig is, in fact, demonic. Once in possession of the wicked hairpiece, Su Hyon begins committing all sorts of transgressions, including causing Chi-Hyon to have a car accident and trying to seduce her boyfriend.', '/dJXuMyqLa3U7DvALOfd4IgOIOXx.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0475591', 'Gharshana', 2004, 'Gautham Menon', '/pIyIJen2DmiuN44ANvHqUx80FeA.jpg', null, 'Rama Chandra (played by Daggubati Venkatesh) is the Deputy Commissioner of Police of Hyderabad City. His upbringing as an orphan, and the special nature of his job as a policeman, have left him reluctant to form relationships with women. He doesn''t have much faith in the judicial system and seeks to ''eliminate'' the criminals in the town through encounters.', '/9Bcf5hEtLvuWrLTJ86z5nU2ZQpo.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0475616', 'Duelist', 2005, 'Myung-se Lee', '/m1cHfY54HVlYPdNYN5szNkNEZtC.jpg', null, 'Set years ago in the era of the Joseon Dynasty, the story follows a young police officer named Namsoon (Ha Ji-won) who, along with her fellow officers, discovers a counterfeit ring operating out of the area they''ve been assigned to protect. However, as the criminals aren''t just printing up their own money, they also plan to use it to topple the economy and take down the government. As her investigative work continues, Namsoon soon makes the acquaintance of a young man known only as Sad Eyes (Gang Dong-won), for the way that he looks out at you from underneath his hair - he doesn''t say much, but he''s got that look and that''s all it takes to pique Namsoon''s interest in him, even if she shouldn''t be thinking those thoughts about someone she might have to toss in jail. Of course, Namsoon can''t deny her feelings even if the object of her affection belongs behind bars, and it''s not long before she''s starting to act on her emotions.', '/ddff8ke8xx3yGGrcr9V2nrjJHsP.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0475627', 'Shikhar', 2005, 'John Mathew Matthan', '/1fh8kxXqyvsEHYPOvkzIctWLg3b.jpg', null, 'Drama - Opposing ideologies are at the heart of this offbeat tale directed by John Mathew Matthan. On one side of the debate is greedy, conniving businessman Gaurav Gupta (Ajay Devgan), who values money above all else; on the other side stands industrial tycoon Srinath Vardhan (Javed Sheikh), who believes in using wealth to benefit society. Caught in the cross fire, unfortunately, is Vardhan''s susceptible son, Jaidev (Shahid Kapoor). -  Ajay Devgn, Shahid Kapoor, Bipasha Basu', '/rJDQqXFq8nXeEe2z9doY7XEhgm1.jpg', null, 0);
