INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0475645', 'Vaah! Life Ho Toh Aisi!', 2005, 'Mahesh Manjrekar', null, null, 'Aditya Singh, fondly called Adi Chachu by the children of his brother, lives in a large mansion in Mumbai with his brother, sister-in-law, sister and grandmother. He is the only one working in the family. He falls in love with a local teacher, Priya. They confess their feelings to each other and plan a wedding. However, before the wedding day Adi is run over by a truck to save Parth and dies.  In afterlife, he meets The Hindu God of Death, Yamraj, a kind-hearted, emotional deity with designer clothes and an red old car. Yamraj allows Adi to go back to Earth as a ghost to stop his evil uncle who wants to sell Adi''s mansion to industrialist Hirachand. Adi enlists the aid of Shakti, a little boy, in order to save his family home.', '/lKQWA0SpcDufXGYeXcT4BGBhMHt.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0475659', 'Mia Sarah', 2006, 'Gustavo Ron', null, null, 'In the half-light of a an old stately home, Samuel Davila and his grandfather Paul, once a revered writer, create schemes to repel the latest in a long line of psychologists come to cure the grandson of his severe agoraphobia: some delicious biscuits with added spice, a refreshing glass of water with added laxative', '/uD8nk1ZNJKg2lRqlJHF29DbNrYK.jpg', null, 0);
