INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0475860', 'Paraiso Travel', 2008, 'Simon Brand', '/ymlxEfvrSkqy2S9P6LqYLaqrV5E.jpg', null, 'PARAISO TRAVEL is the story of Marlon Cruz, a young Colombian man who, motivated by his girlfriend Reina leaves his comfortable life in Medellin and flees with her through Guatemala and Mexico, across the borders, illegally into the United States. Marlon''s journey, which takes him through seedy strip clubs, dance halls, homeless hostels, ultimately takes him to a place he could have not predicted', '/fBIDSOFrHgaBRXa9e1xFmPgiM9I.jpg', null, 0);
