INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0478093', 'All About Darfur', 2005, 'Taghreed Elsanhouri', null, null, 'Civil strife in Sudan is explored in personal detail in this film from British-Sudanese filmmaker Taghreed Elsanhouri. Eschewing the nightmarish footage so prevalent on nightly newscasts to instead focus on the personal stories of those who have witnessed firsthand the horrors of genocide, Elsanhouri turns her lens on the troubled citizens of Sudan in a bid to understand their plight on a more humane level. By opening the lines of communication with her fellow Sudanese and offering a platform to voice their suggestions for building a brighter future, Elsanhouri exposes truths rarely discussed by the mainstream media.', '/j5kZfKNBLql2Ga0PYiu9HZuIIAj.jpg', null, 0);
