INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0478149', 'Fade to Black', 2006, 'Oliver Parker', '/ydA3gHp6CNyQrvSDC2y4AFDNBml.jpg', null, 'Still reeling from the painful breakup of his marriage to screen siren Rita Hayworth, iconic filmmaker Orson Welles (Danny Huston) makes his way to Rome, where he gets pulled into a tangled political plot involving murder and mysterious motives. A beautiful actress (Paz Vega) proves a tempting distraction. But if they want to stay alive, Welles and his young Italian driver (Diego Luna) need to stay focused.', '/csBkJaEdnOUh6y04ydLXyHNHKXp.jpg', null, 0);
