INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0478157', 'The Giant Buddhas', 2005, 'Christian Frei', null, null, 'Christian Frei''s documentary traces the tragic tale of the giant Buddhas of Afghanistan''s Bamiyan Valley, which stood as monumental landmarks for 1,500 years until 2001, when the Taliban declared that all non-Islamic statues in the country be destroyed. Despite international protest, the statues were blown up. Through interwoven narratives from past and present, Frei''s film sheds light on the disturbing consequences of religious fanaticism.', '/tOwxcyfyr5q6wianJZG7776734.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0478160', 'Into Great Silence', 2005, 'Philip Gröning', '/ipvg0rdMuFvdIK3eaL1oOa6NW6s.jpg', null, 'Into Great Silence (German: Die GroÃŸe Stille) is a documentary film directed by Philip GrÃ¶ning that was first released in 2005. It is an intimate portrayal of the everyday lives of Carthusian monks of the Grande Chartreuse, high in the French Alps (Chartreuse Mountains). The idea for the film was proposed to the monks in 1984, but the Carthusians said they wanted time to think about it. The Carthusians finally contacted GrÃ¶ning 16 years later to say they were now willing to permit GrÃ¶ning to shoot the movie, if he was still interested.', '/kBM1RBECriMqPQW7XrJ1lgWZuc4.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0478165', 'The Butcher', 2006, 'Edward Gorsuch', '/qoJ903NP49sBhgBqZ41UiOuPiy0.jpg', null, 'A tragic car accident in the middle of nowhere finds six stranded college students fighting for their lives after making a horrifying discovery in a remote farmhouse in director Edward Gorsuch''s rural frightener. With no place to run and no police station for miles, these doomed students are about to discover that down home hospitality is the last thing on the minds of the murderous family who stalks them through the woods.', '/8G5F6iyM7uSnq6r2jW6IyQdI4N2.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0478166', 'The Heart of the Game', 2005, 'Ward Serrill', '/x7zAztd3VK0dmWfFiekw3WG66mj.jpg', null, 'The Heart of the Game captures the passion and energy of a Seattle high school girls'' basketball team as they strive to win the state championship, the eccentricity of their unorthodox coach, and the incredible true story of one player''s fight to play the game she loves.', '/t7QYFlwyfM7PeO7rA9wiuZWkoly.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0478175', 'I Am', 2005, 'Dorota Kedzierzawska', '/ms1xpuVYUf3CF3G0uDCDeumn0y6.jpg', null, '"I am" tells the story of Kundel, a boy searching for his place in life, his identity. After running away from an orphanage where he is treated as an outcast among outcasts by both his peers and the adults in charge, and being rejected a second time by his alcoholic mother, who had earlier abandoned him, the resolute 11-year-old finds a "home" on a deserted old barge on the outskirts of his hometown. He is still an outcast, but he is an outcast on his own terms, rejecting both patronizing charitable gestures and coercion by local thugs. In the process he makes one true friend, a girl from an affluent family who is also an outcast, despite living in material comfort.', '/tKbUfli2Zu3u6zxysLNBowvX1ik.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0478176', 'John & Jane', 2005, 'Ashim Ahluwalia', null, null, 'A fresh new blend of observational documentary and tropical science-fiction, JOHN &amp; JANE follows the stories of six call agents that answer American 1-800 numbers in a Mumbai call center. After a heady mix of American culture training and 14 hour night shifts, the job soon starts to take its toll. Counter pointing the fluorescent interiors of late night offices and hyper-malls with the uneasy currents swirling around the characters, JOHN &amp; JANE discovers a young generation of urban Indians that are beginning to live between the real and the virtual. However, this futuristic world of American aliases and simulated reality is not science fiction, these are the times in which we live. JOHN &amp; JANE raises disturbing questions about the nature of personal identity in a 21st century globalised world.', '/bk2rOFpTMlICumGBgD5waGLGBK.jpg', null, 0);
