INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0492663', 'Princess in an Iron Helmet', 2006, 'Kaze Shindô', '/85WCshVyWdBe4tWKEHM7ZLbvPFx.jpg', null, 'The second feature from female director Shindo Kaze (Love/Juice), Princess in an Iron Helmet (a.k.a. Korogare! Tamako) is a bright and colorful youth film about a quirky young woman''s reluctant coming-of-age journey. Tamako (Yamada Maiko) is 20 years old, lives at home with her mother (Kishimoto Kayoko) and teenaged brother, and simply refuses to grow up. Ever since her father (Naoto Takenaka) left the family, she''s stopped trusting people, and now lounges around in her own small, claustrophobic world. Wearing an iron helmet whenever she steps outside, she ventures only to her mom''s beauty salon, the mechanic shop, and the bakery - the source of her greatest joy in life, sweet buns. Her world is static, insular, and safe, and there''s no need to change - that is until her cat runs away, the bakery closes, and her mom falls in love with another man.', '/ud6jpQq1XjRl2bvb4LPl0E6P18x.jpg', null, 0);
