INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0499375', 'Guru', 2007, 'Mani Ratnam', '/w4CLEMCuimirZONbfHLRRA2W8yj.jpg', null, 'Gurukant Desai hails from Idhar, a small village in Gujarat, but dreams of setting up his own business in Mumbai. After he returns from Turkey, he marries Sujatha for getting the dowry and arrives in Mumbai to start his business. This film chronicles the obstacles he meets, his subsequent rise and the huge backlash he receives when it is revealed that he used unethical means to rise in the business circuit.', '/l8mS8ab6iI3y2YgiUZJoQaQn5ES.jpg', null, 0);
