INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0499455', 'Day Night Day Night', 2006, 'Julia Loktev', '/f4NcrIFikeYiiMBQTvTyO6fsMQF.jpg', null, 'A 19 year-old girl prepares to become a suicide bomber in Times Square. She speaks with no accent so it''s impossible to pinpoint her ethnicity. And we never learn why she made her decision. We don''t know whom she represents, what she believes in--we only know she believes it absolutely.', '/psIfot9bFtrPrcCVRDhQr7z6pNl.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0499456', 'Days of Darkness', 2007, 'Jake Kennedy', '/uALIMm5hphfi7k77TNhyEEExyQp.jpg', null, 'When a comet strikes Earth and kicks up a cloud of toxic dust, hundreds of humans join the ranks of the living dead. But there''s bad news for the survivors: The newly minted zombies are hell-bent on eradicating every last person from the planet. For the few human beings who remain, going head to head with the flesh-eating fiends is their only chance for long-term survival. Yet their battle will be dark and cold, with overwhelming odds.', '/tWCyKXHuSrQdLAvNeeVJBnhf1Yv.jpg', null, 0);
INSERT INTO movies (id, title, year, director, backdrop_path, budget, overview, poster_path, revenue, hidden) VALUES ('tt0499467', 'Elipsis', 2006, 'Eduardo Arias-Nath', null, null, 'Parallel and fragmented stories are linked by the relationship between the famous actor and his friend Sebastian Castillo Galo Vidal, a frustrated fashion designer. The roles are reversed as a result of decisions made each of them. Sebastian''s life is now in chaos, while the Galo is a success. Their lives are again when Sebastian goes to Gaul for help and the two embark on a journey that will put their friendship and their lives at stake.', '/mdWreNC2Q6gT2sqlMSNQsV92mIZ.jpg', null, 0);
